// learn-to-code/src/pages/Setup/Setup.js

import React from 'react';
import DarkModeToggle from '../../../components/DarkModeToggle/DarkModeToggle';
import TableOfContents from '../../../components/TableOfContents/TableOfContents';
import './Setup.css';

function Setup() {
    const sections = [
        { id: 'intro', title: 'Introduction' },
        { id: 'intro-1', title: '1.1 Overview', subsection: true },
        { id: 'install-python', title: 'Install Python' },
        { id: 'install-pip', title: 'Install pip' },
        { id: 'install-vscode', title: 'Install Visual Studio Code' },
        { id: 'install-extensions', title: 'Install Extensions' },
        { id: 'summary', title: 'Summary' },
    ]

    return (
        <div className="setup-container">
            <header className="header">
                <h1 className="fade-in-header">Let's get started</h1>
                <DarkModeToggle />
            </header>
            <div className="content-container">
                <TableOfContents sections={sections} />
                <main className="main-content">
                    <section id="intro">
                        <h2>Introduction</h2>
                    </section>
                        <section id='intro-1'>
                            <h3>Overview</h3>
                            <p>
                                Before we start writing Python code, we need to set up our development environment. This includes installing Python, a code editor, and some extensions.
                            </p>
                    </section>
                </main>
            </div>
        </div>
    );
}

export default Setup;
